  //获取数据
  void getHttp() async {
    try {
      HttpManager().getOld(
        url: Api.COMPOUND_INFO,
        params: {"id": molId},
        successCallback: (data) {
          var json= jsonDecode(data);
          ProductBaseDetailData baseDetail= ProductBaseDetailData.fromJson( json['compound']);
          setState(() {
            product = baseDetail;
            product.baikeInfos.removeWhere((element) => element.nameUrl=='bianhao');
            for(int i = 0; i < product.baikeInfos.length; i++){
              product.baikeInfos[i].isExpanded = false;
            }
            baikeInfos=product.baikeInfos;
          });
        },
        errorCallback: (HttpError error) {
          print(error.message);
        },
        tag: "compound_info",
      );
      HttpManager().getOld(
        url: Api.COMPOUND_BAIKE_INFO,
        params: {"id": molId},
        successCallback: (data) {
          var json= jsonDecode(data);
          ProductDetailData pDetail = ProductDetailData.fromJson(json["baike_info"]);
          setState(() {
            productDetail =pDetail;
          });
        },
        errorCallback: (HttpError error) {
          print(error.message);
        },
        tag: "compound_baike_info",
      );
      SharedPreferences prefs = await SharedPreferences.getInstance(); //初始化
      String sn_api = prefs.getString('SN_API');
      HttpManager().getOld(
        url: Api.FAVORITES_BAIKE_STATUS,
        params: {"mol_ids": molId},
        options: Options(
            headers: {
              'Auth-Basic-Token':sn_api,
            }
        ),
        successCallback: (data) {
          var json= jsonDecode(data);
          var stateCode=json['status']['$molId'];
          String status = stateCode.toString();
          setState(() {
            baikeStatus =status;
          });
        },
        errorCallback: (HttpError error) {
          print(error.message);
        },
        tag: "favorites_baike_status",
      );

    } catch (e) {
      print(e);
    }

  }
  //关注取消关注
  void getFavoritesHttp() async {
    try {
      SharedPreferences prefs = await SharedPreferences.getInstance(); //初始化
      String sn_api = prefs.getString('SN_API');
      if(sn_api != null && sn_api !='null'){
        if(baikeStatus == '2'){
          //关注
          HttpManager().postOld(
            url: Api.FAVORITES_BAIKE,
            data: {"mol_id": molId},
            options: Options(
                headers: {
                  'Auth-Basic-Token':sn_api,
                }
            ),
            successCallback: (data) {
              YToast.show(context: context, msg: YStrings.AddSucessStr);
              var json= jsonDecode(data);
              if(json['ok']){
                setState(() {
                  baikeStatus ='1';
                });
              }
            },
            errorCallback: (HttpError error) {
              print(error.message);
            },
            tag: "favorites_baike",
          );
        }else{
          //取消关注
          HttpManager().deleteOld(
            url: Api.FAVORITES_BAIKE,
            data: {"mol_ids": molId},
            options: Options(
                headers: {
                  'Auth-Basic-Token':sn_api,
                }
            ),
            successCallback: (data) {
              YToast.show(context: context, msg: YStrings.cancelSucessStr);
              var json= jsonDecode(data);
              if(json['ok']) {
                setState(() {
                  baikeStatus = '2';
                });
              }
            },
            errorCallback: (HttpError error) {
              print(error.message);
            },
            tag: "favorites_baike_cancel",
          );
        }
      }else{
        //跳转登录
        Navigator.push(
          context,
          MaterialPageRoute(builder: (context) =>
            LoginState()
          ),
        );
      }


    } catch (e) {
      print(e);
    }
  }
}
